        const [users, posts] = await Promise.all([
            fetch('/api/users').then(r => r.json()),
            fetch('/api/posts').then(r => r.json())
        ]);

        console.log('Users:', users.length);
        console.log('Posts:', posts.length);

        results.users = users;
        results.posts = posts;
